sys_script_include.script:
var EventUtilsClient = Class.create();
EventUtilsClient.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	FetchEventDetailsByEventId:function(){
		var eventid= this.getParameter('sysparam_eventid');
		var eventgr= new GlideRecord('x_1791991_event_ma_event_record ');
		if(eventgr.get(eventid)){
		var result={
			eventname : eventgr.x_event_name,
			location : eventgr.x_location,
			eventdate : eventgr.x_event_date,
			status : eventgr.x_status,
			contactperson : eventgr.x_contact_person
		};
		return result;
		}
		return "Event Id is Invalid";
	},

	CheckEventVip: function(){
		var eventid= this.getParameter('sysparam_eventid');
		var eventgr= new GlideRecord('x_1791991_event_ma_event_record ');
		if(eventgr.get(eventid)){
		if(eventgr.vip){
			return true;
		}
		else{
			return false;
		}
		}
		return "Event Id is Invalid";
	},
    type: 'EventUtilsClient'
});
