sys_script_include.script:
var FeedbackUtils = Class.create();
FeedbackUtils.prototype = {
    initialize: function(eventid) {
        this.eventid = eventid;
    },
	getFeedback:function(id){
        var res = [];
        var feedback = new GlideRecord('x_1791991_event_ma_feedback');
        feedback.addQuery('number', id);
        feedback.query();
        if (feedback.next()) {
            res.push({
                id: feedback.getDisplayValue(),
				rating : feedback.getValue('x_rating'),
            });}
            return JSON.stringify(res);

    },
	checkFeedback:function(id){
        var feedback = new GlideRecord('x_1791991_event_ma_feedback');
        feedback.addQuery('number', id);
        feedback.query();
        if (feedback.next()) {
			if(feedback.getValue('x_rating')== 'excellent'||feedback.getValue('x_rating')== 'good')
			{
				return 'positive';
			}
		}
		return 'negative';
		},
		getEventFeedback:function(){
        var res = [];
        var feedback = new GlideRecord('x_1791991_event_ma_feedback');
        feedback.addQuery('x_event_id.number', this.eventid);
        feedback.query();
        if (feedback.next()) {
            res.push({
                id: feedback.getDisplayValue(),
				rating : feedback.getValue('x_rating'),
            });}
            return JSON.stringify(res);
    },
	fetchAllFeedback: function() {
        var res = [];
        var gr = new GlideRecord('x_1791991_event_ma_feedback'); // Use your actual feedback table name
        gr.query();
        while (gr.next()) {
            res.push({
                number: gr.getValue('number'),
                attendee: gr.getDisplayValue('x_attendee_id'), // Use getDisplayValue if attendee_id is a reference
                rating: gr.getValue('x_rating'),
                comments: gr.getValue('x_comments')
            });
        }
        return JSON.stringify(res);
    },

    type: 'FeedbackUtils'
};
